import React, { useEffect, useState } from "react";
import App from "../App";
import "./css/style.css";

const Tempapp =() => {

    const [city, setCity] = useState(null);
    const [search, setSearch] = useState("mumbai");
    useEffect( () =>{
        const fetchApi = async () =>{
            const url = `https://api.openweathermap.org/data/2.5/weather?q=${search}&appid=5097a176a6ef637720140b0f79f1d420`
            const response = await fetch(url);

            const resJson = await response.json();
            setCity(resJson);



        }
        fetchApi();
    })


    return(
        <>
        <div className="box">
            <div className="inputData">
                <input type="search" 
                className="inputFeild"  onChange={(event)  => {setSearch(event.tar)

                }}/>

            </div>
            <div className="info">
                <h2 className="location">
                <i className ="fas fa-street-view"></i>{search}

                </h2>
                <h1 className="temp">5</h1>
                <h3 className="tempmin_max"> Min : 5.25 cel | Max : 5.25 cel</h3>

            </div>

            <div className= "wave -one"></div>
            <div className= "wave -two"></div>
            <div className= "wave -three"></div>

        </div>
        </>
    )
}
export default Tempapp;
